import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const WeatherApp = () => {
  const [unit, setUnit] = useState('C');
  const [city, setCity] = useState('New York');
  const [weatherData, setWeatherData] = useState(null);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3007/api/weather?city=${city}`);
      console.log(response.data);
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, [city, unit]);

  const toggleTemperatureUnit = () => {
    setUnit((prevUnit) => (prevUnit === 'C' ? 'F' : 'C'));
  };

  return (
    <div className="App">
      {/* Your existing code */}
      <div className='fare-celc'>
        <div className='farenheight' onClick={toggleTemperatureUnit}>
          F
        </div>
        <div>|</div>
        <div className="celcius" onClick={toggleTemperatureUnit}>
          C
        </div>
      </div>

      {/* Display temperature using the selected unit */}
      <div className='top-left'>
        <div className='deg-temp'>
          {/* Assuming weatherData is not null and has the required data structure */}
          <div className='top-degree'>{Math.floor(weatherData?.forecast[0].temperature)}{unit}Â°</div>
        </div>
      </div>

      {/* Cards */}
      <div className="lower-info">
        <div className="cards">
          {weatherData?.forecast.slice(0, 7).map((day, index) => (
            <div className='card' id={`card-${index + 1}`} key={index}>
              <div className='day'>{day.day}</div>
              {/* Assuming you have a function to map weather icon codes to image paths */}
              <img src={getWeatherIcon(day.icon)} alt='' id='image' />
              <div className="degrees">
                <div className="upper-deg">{Math.floor(day.tempMax)}{unit}</div>
                <div className="lower-deg">{Math.floor(day.tempMin)}{unit}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default WeatherApp;
